    [TestClass()]
    public class InputDigesterTests
    {
        [TestMethod()]
        public void DigestSymbolInputTest()
        {
            string input = "Ընկերությունը անգին գանձ է։ Այն նման չէ թանկարժեք ծաղկամանի, որն օգտագործվում է պարզապես դեկորացիայի համար։ Ընկերությունն ավելի շատ նման է գեղեցիկ ծաղկի, որին անհրաժեշտ է ջուր և խնամք, որպեսզի շարունակ փարթամանա ու ծաղկի։ Աբրահամը թանկ էր գնահատում Եհովայի հետ իր ընկերությունը և ամեն բան անում էր այն պահպանելու համար։ Իսկ ինչպե՞ս։ Աբրահամը երբեք չմտածեց, թե բավական է՝ ինչքան աստվածավախություն և հնազանդություն է դրսևորել։ Երբ նա իր մեծ ընտանիքով ճամփորդում էր դեպի Քանան, շարունակում էր հետևել Եհովայի առաջնորդությանը իր բոլոր որոշումներում՝ մեծ թե փոքր։ Իսահակի ծնվելուց մեկ տարի առաջ, երբ Աբրահամը 99 տարեկան էր, Եհովան պահանջեց, որ նրա տան բոլոր տղամարդիկ թլփատվեն։ Արդյո՞ք Աբրահամը հարցականի տակ դրեց այդ պատվերը կամ արդարացում գտավ չհնազանդվելու համար։ Ո՛չ, նա վստահեց Եհովային և «հենց նույն օրը» նրա ասածի պես արեց";

            FrequencyResultSet result = InputDigester.DigestInput(input, DigestType.Symbols);

            StringBuilder resultString = new StringBuilder();

            foreach(var symbol in result.FoundSymbolFrequencyResultSet.SymbolDictionary)
            {
                resultString.AppendLine($"{symbol.Value.FoundSymbol.Symbol}: {symbol.Value.Hits}");
            }

            Assert.IsNotNull(result);
        }

        [TestMethod()]
        public void DigestWordInputTest()
        {
            string input = "կառքերն ու պսակ պաշտպանություն են մեզ համար այդպես կլինի եթե դուք լսեք ձեր աստված եհովայի ձայնին զաք  երգ   ինչ կպատասխանես զաքարիայի վերջին տեսիլք ինչում էր վստահեցնում աստծու ժողովրդին ինչպես ենք քաջալերվում կարդալով պղնձե երկու լեռների և կառքերի մասին ինչ ես վճռել անել բոլոր ժամանակների մեծագույն շինարարության գործում  ինչ իրավիճակ էր տիրում երուսաղեմում զաքարիային տրված յոթերորդ տեսիլքից հետո յոթերորդ տեսիլքից հետո զաքարիա մարգարեն մտածելու շատ բան ուներ եհովան հավաստիացրել էր որ անազնիվ մարդկանց պատասխանատվության է կանչելու իրենց անօրենությունների համար այս խոստումն ինչ խոսք զորացրեց մարգարեին սակայն ոչինչ չփոխվեց մարդիկ շարունակում էին իրենց անազնիվ ու անօրեն գործեր եհովայի տաճարի վերակառուցման ավարտ հորիզոնում չէր էլ երևում հրեաներն ինչպես կարող էին այդքան արագ թողնել աստծու հանձնարարած գործ արդյոք նրանք հայրենիք էին վերադարձել լոկ անձնական շահի համար զաքարիան վստահ էր որ երուսաղեմ եկած մարդիկ մեծ հավատ ունեին չէ որ նրանք թողել էին իրենց համար հարազատ դարձած մի երկիր ու տեղափոխվել մի վայր որ նրանցից շատեր երբեք չէին էլ տեսել ճշմարիտ աստվածն արթնացրել էր նրանց ոգին որ թողնեին իրենց ապահով կյանք տներն ու աշխատանք եզր   ի վերջո եթե այս մարդկանց համար կարևոր չլիներ եհովայի տաճարի վերակառուցում նրանք չէին կատարի այս ճանապարհորդություն չէին անցնի մոտ  կիլոմետր  մղոն անհարմար ճանապարհներով  ինչ խոչնդոտների առաջ կանգնեցին հայրենիք վերադարձած հրեաներ պատկերացրու ինչպիսին կարող էր լինել ճամփորդություն դեպի հայրենի երկիր ճանապարհին հրեաներ բավականին ժամանակ ունեին մտածելու իրենց նոր տան մասին նրանք լսել էին թե ինչ գեղեցիկ քաղաք է եղել երուսաղեմ տարեցներ նույնիսկ տեսել էին տաճարի նախկին փառք եզր  եթե դու էլ նրանց հետ երուսաղեմ գնալիս լինեիր ինչ կզգայիր երբ առաջին անգամ տեսնեիր այդ քաղաք քո նոր տուն սիրտդ կտրտմեր երբ տեսնեիր ավերված շինություններ մոլախոտերով ծածկված երուսաղեմի քարուքանդ պարիսպներ նկած դարպասներն ու աշտարակներ կհամեմատեիր բաբելոնի հսկայական կրկնակի պարիսպների հետ սակայն ժողովուրդ չհուսահատվեց նրանք տունդարձի երկար ճանապարհին արդեն տեսել էին եհովայի փրկարար ձեռք ուստի հասնելուն պես զոհասեղան կանգնեցրին նախկին տաճարի գտնվելու վայրում և սկսեցին ամեն օր զոհեր մատուցել եհովային եզր  ամեն ինչ այնպես էր նթանում որ թվում էր թե ոչինչ չի կոտրի նրանց վճռականություն տաճարի աշխատանքներից բացի իսրայելացիներ պետք է նաև վերակառուցեին իրենց քաղաքներ պետք է տներ շինեին արտեր մշակեին և կերակրեին իրենց նտանիքներ եզր  ինչ խոսք աշխատանք հսկայական էր սակայն շատ չանցած հակառակություն սկսվեց թեև նրանք հաստատակամությամբ էին սկսել իրենց գործ տարիների թշնամանք ջլատեց նրանց ուժեր եզր – իսկ երբ մ թ ա ին պարսից թագավորն արգելեց շարունակել երուսաղեմի վերակառուցման աշխատանքներ դա ասես վերջնական հարված լիներ քաղաքի ապագան անորոշության մշուշով պատվեց եզր – եհովան ինչ արեց որ քաջալերի ու գործի մղի իր ժողովրդին եհովան գիտեր ինչ է պետք իր ժողովրդին ուստի զաքարիային ևս մեկ տեսիլք տվեց վերջին տեսիլք որպեսզի հավաստիացնի որ սիրում է հրեաներին ցույց տա իր գնահատանք նրանց արած ամեն բանի համար և վստահեցնի որ կպաշտպանի նրանց եթե շարունակեն կատարել իր տված հանձնարարություն տաճար վերաշինելու վերաբերյալ եհովան խոստացավ այդպես կլինի եթե դուք լսեք ձեր աստված եհովայի ձայնին զաք  հրեշտակների զորք ա ինչպես է սկսվում զաքարիայի ութերորդ տեսիլք տես հոդվածի սկզբի նկար բ ինչու են ձիեր տարբեր գույների զաքարիային տրված վերջին ութերորդ տեսիլք հավանաբար ամենից շատ է հավատ զորացնում կարդա զաքարիա – պատկերացրու տեսարան պղնձե երկու լեռների արանքից չորս կառքեր են դուրս գալիս ու առաջ սլանում ասես մարտի համար պատրաստված կառքերին տարբեր գույնի ձիեր են լծված ամեն կառքին մի գույնի ինչի շնորհիվ մի կառք զատորոշվում է մյուսից տեսնելով սա զաքարիան հարցնում է սրանք ինչ են զաք  մեզ ևս հետաքրքրում է այս հարցի պատասխան քանի որ տեսիլք անմիջականորեն մեզ է առնչվում եհովան շարունակում է իր հրեշտակների միջոցով պաշտպանել ու զորացնել իր ժողովրդին  ա ինչ են ներկայացնում երկու լեռներ բ ինչու են լեռներ պղնձից աստվածաշնչում լեռներ կարող են ներկայացնել թագավորություններ կամ կառավարություններ զաքարիայի տեսիլքի լեռներ նման են այն երկու լեռներին որոնց մասին խոսվում է դանիելի մարգարեության մեջ մի լեռ ներկայացնում է ողջ տիեզերքում եհովայի հավիտենական իշխանություն իսկ մյուս հիսուսի մեսիական թագավորություն դան   այն ժամանակվանից սկսած երբ հիսուս թ աշնան գահ նստեց այս երկու լեռներ միասին կարևոր դեր են խաղում երկրի վրա աստծու կամքի իրագործման մեջ իսկ ինչու են լեռներ պղնձից ոսկու պես պղինձ փայլուն թանկ գնահատվող մետաղ է եհովան պատվիրել էր այն օգտագործել խորանի և հետագայում երուսաղեմի տաճարի կառուցման ժամանակ ելք – թագ – ուստի այն փաստ որ լեռներ պղնձից են նդգծում է եհովայի գերիշխանության և մեսիական թագավորության գերազանցություն դրանք ողջ մարդկության համար ապահովություն և օրհնություններ են բերելու ովքեր են վարում կառքեր և ինչ հանձնարարություն ունեն նրանք այժմ անդրադառնանք կառքերին ինչ են ներկայացնում կառքերն ու կառապաններ կառքեր վարողներ հրեշտակներ են հավանաբար նրանցից կազմված զորախմբեր կարդա զաքարիա – նրանք հատուկ հանձնարարությամբ դուրս են եկել ամբողջ երկրի տիրոջ առջևից ինչ է նրանց հանձնարարված կառքերն ուղարկվում են տարբեր տարածքներ որպեսզի պաշտպանեն եհովայի ժողովրդին հատկապես հյուսիսի երկրից բաբելոնից եհովան այնպես էր անելու որ իր ժողովուրդն այլևս բաբելոնի գերության մեջ չնկներ պատկերացրու որքա՜ն սա մխիթարեց տաճար վերակառուցողներին այլևս կարիք չկար վախենալու թշնամիների միջամտությունից զաքարիայի տեսիլք ինչում է մեզ վստահեցնում ինչպես զաքարիայի օրերում այնպես էլ այսօր զորքերի տեր եհովան իր հրեշտակների միջոցով պաշտպանում ու զորացնում է իր ժողովրդին մաղ  եբր   թից սկսած երբ հոգևոր իսրայել ազատվեց մեծ բաբելոնի խորհրդանշական գերությունից ճշմարիտ երկրպագություն ծաղկում է չնայած անդադար հալածանքներին հայտն  քանի որ մենք ունենք հրեշտակների պաշտպանություն չպետք է վախենանք թե եհովայի կազմակերպություն մի օր նորից հոգևոր գերության մեջ կնկնի սաղ  փոխարեն կարող ենք վստահ լինել որ աստծու ծառաներ ամբողջ աշխարհում կշարունակեն հոգևորապես բարգավաճել զաքարիայի տեսիլքի շուրջ խորհելով համոզվածությամբ ենք լցվում որ պղնձե երկու լեռների շուքի տակ մենք ապահով ենք ինչու չպետք է վախենանք այն հարձակումից որ գալու է աստծու ժողովրդի վրա շատ շուտով սատանայի աշխարհի քաղաքական ուժեր կոալիցիա կկազմեն որպեսզի ոչնչացնեն աստծու ժողովրդին եզեկ  –դան   հայտն  եզեկիելի մարգարեության մեջ այդ ուժեր նկարագրվում են որպես երկիր ծածկող ամպեր այնտեղ նաև ասվում է որ ձիեր հեծած նրանք կատաղած կգան մեզ վրա եզեկ  արդյոք պետք է վախենանք բոլորովին մեր թիկունքում հրեշտակների մեծ բանակ է կանգնած մեծ նեղության այդ թեժ պահին զորքերի տեր եհովայի հրեշտակներ կհավաքվեն որ պաշտպանեն նրա ժողովրդին ու կործանեն նրա գերիշխանության հակառակվողներին  թեսաղ  ի՜նչ փառահեղ կլինի այդ օր իսկ ով է առաջնորդելու եհովայի այդ երկնային զորքին եհովան պսակ է դնում թագավորի ու քահանայի գլխին  ա եհովան ինչ է պատվիրում զաքարիային բ որտեղից գիտենք որ նձյուղ անունով անձնավորություն ներկայացնում է հիսուս քրիստոսին եհովայի տված ութ տեսիլքներ միայն զաքարիան տեսավ այժմ աստված նրան պատվիրում է մարգարեական նշանակություն ունեցող մի բան անել ինչ տեսանելի կլինի ուրիշների համար դա կքաջալերի տաճար վերաշինողներին կարդա զաքարիա – եհովան մարգարեին ասում է որ այն նվիրաբերությունների միջից որոնք իրենց հետ բերել էին բաբելոնից նոր վերադարձած մարդիկ հելդան տուբիան ու հեդայիան արծաթ ու ոսկի վերցնի և մի վեհաշուք պսակ շինի զաք  արդյոք զաքարիան այդ պսակ պետք է դներ զորաբաբել կառավարչի գլխին որ հուդայի ցեղից էր ու դավթի սերնդից ոչ ժողովուրդ գուցե զարմացավ տեսնելով որ զաքարիան պսակ դնում է հեսու քահանայապետի գլխին արդյոք դա նշանակում էր որ հեսու քահանայապետ թագավոր դարձավ ոչ նա դավթի արքունական սերնդից չէր ուստի չէր կարող թագավոր լինել նրա գլխին պսակ դնել մարգարեական նշանակություն ուներ դա մատնանշում էր ապագա հավիտենական թագավորին ու քահանային թագավոր դարձող քահանայապետի անուն նձյուղ է սուրբ գիրք բացահայտում է որ նձյուղ հիսուս քրիստոսն է ես  մատթ  ինչ է անում հիսուս որպես թագավոր ու քահանայապետ գործելով որպես թագավոր ու քահանայապետ հիսուս առաջնորդում է եհովայի երկնային զորքին նա ամեն ինչ անում է որպեսզի աստծու ժողովուրդ որպես ամբողջություն պաշտպանված լինի թշնամանքով լի այս աշխարհում երեմ  մոտ ապագայում քրիստոս հաղթելով ազգերին կարդարացնի աստծու գերիշխանություն և կպաշտպանի նրա ժողովրդին հայտն –  սակայն մինչ այդ նձյուղ մեկ ուրիշ կարևոր բան պետք է իրագործի նա կառուցելու է տաճար  ա մեր ժամանակներում վերականգնման ու զտման ինչ աշխատանքներ են արվել և ով է դա արել բ ինչ իրագործված կլինի քրիստոսի հազարամյա իշխանության վերջում թագավոր ու քահանայապետ լինելուց բացի հիսուս պետք է կառուցեր եհովայի տաճար կարդա զաքարիա  այդ գործ ներառում էր աստծու ճշմարիտ երկրպագուներին մեծ բաբելոնից ազատել և քրիստոնեական ժողով վերականգնել ինչ տեղի ունեցավ թին այդ տարի հիսուս նաև նշանակեց հավատարիմ և իմաստուն ծառային որպեսզի վերջինս առաջնորդի այն գործ որն արվում է հոգևոր մեծ տաճարի երկրային գավիթներում մատթ  բացի այդ հիսուս զտում է աստծու ժողովրդին օգնելով որ մաքուր երկրպագություն մատուցեն մաղ – հազարամյա իշխանության ժամանակ հիսուս իր  թագավորակիցների ու քահանայակիցների հետ կօգնի հավատարիմ մարդկանց հասնել կատարելության դրանից հետո մաքրված երկրի վրա միայն կմնան աստծու ճշմարիտ երկրպագուներ այդ ժամանակ ճշմարիտ երկրպագություն վերջապես լիովին վերականգնված կլինի մասնակցիր շինարարության ինչում եհովան վստահեցրեց հրեաներին և դա ինչ ազդեցություն թողեց նրանց վրա զաքարիայի հաղորդած լուր ինչ ազդեցություն թողեց նրա օրերում ապրող հրեաների վրա եհովան խոստացավ որ կօգնի ու կպաշտպանի նրանց որ վերակառուցեն տաճար այդ խոստում հույսով լցրեց նրանց վհատված սրտեր բայց ինչպես էին հրեաներ քիչ մարդկանցով կատարելու այդ մեծ գործ զաքարիայի մյուս խոսքեր փարատեցին նրանց բոլոր կասկածներն ու վախեր եհովան հայտնեց որ հելդայից տուբիայից ու հեդայիայից բացի ուրիշ հավատարիմ մարդիկ էլ էին գալու և շինելու իր տաճար կարդա զաքարիա  ուստի վստահ լինելով որ աստված կաջակցի իրենց հրեաներ իսկույն գործի անցան ու չնայած արգելքին վերսկսեցին տաճարի շինարարություն շուտով եհովան վերացրեց ամենամեծ խոչնդոտ թագավորի դրած արգելք և մ թ ա ին տաճարի վերակառուցումն ավարտվեց եզր  զաք  սակայն եհովայի ասած խոսքեր վերաբերում են ավելի մեծ գործի որն արվում է մեր օրերում  եհովան երբեք չի մոռանա այն սեր որ դրսևորում ենք նրա հանդեպ տես պարբերություն  ինչպես է մեր օրերում կատարվում զաքարիա  այսօր միլիոնավոր մարդիկ ճշմարիտ երկրպագության կողմն են կանգնել նրանք իրենց արժեքավոր բաներ ժամանակ ուժերն ու միջոցներ հոժար սրտով նվիրում են ի նպաստ եհովայի հոգևոր մեծ տաճարի առակ  ինչպես կարող ենք վստահ լինել որ եհովան գնահատում է մեր հավատարիմ աջակցություն հիշիր որ հելդան տուբիան ու հեդայիան թանկարժեք նյութեր բերեցին որոնցից զաքարիան պսակ շինեց հետագայում այդ պսակ ծառայեց որպես հիշեցում այն ամենի մասին ինչ նրանք արել էին ճշմարիտ երկրպագության համար զաք  նույն ձևով էլ եհովայի հանդեպ մեր ունեցած սեր և նրա համար արած գործ երբեք չեն մոռացվի եբր  դրանք երբեք չեն ջնջվի եհովայի հիշողությունից զաքարիայի տեսիլքներ մեզ ինչ անելու պետք է մղեն այն ամեն ինչ այս վերջին օրերում արվում է ճշմարիտ երկրպագության համար եհովայի օրհնության և քրիստոսի առաջնորդության վառ ապացույցն է մենք կայուն ապահով և հավիտենական կազմակերպության մաս ենք կազմում մաքուր երկրպագության վերաբերյալ եհովայի նպատակ անպայման կիրագործվի ուստի թանկ գնահատիր եհովայի ժողովրդի մեջ լինելու քո առանձնաշնորհում և միշտ լսիր նրա ձայնին եթե այդպես վարվես մեր թագավորն ու քահանայապետ ինչպես նաև երկնային կառքեր վարող հրեշտակներ կպաշտպանեն քեզ ձգտիր հնարավորինս լիարժեքորեն աջակցել ճշմարիտ երկրպագության այդ դեպքում կարող ես համոզված լինել որ զորքերի տեր եհովան քեզ իր հովանու տակ կպահի այս աշխարհի վերջին օրերում և ողջ հավիտենության նթացքում լրացուցիչ տեղեկությունների համար տես դիտարան մայիսի էջ  նթերցողների հարցեր մատթեոս ում նազովրեցի թարգմանված բառ ծագում է եբրայերեն մի բառից որ նշանակում է նձյուղ ";

            FrequencyResultSet result = InputDigester.DigestInput(input, DigestType.Words);

            StringBuilder resultString = new StringBuilder();

            resultString.AppendLine(@"<table style=""width: 100 % "">");
            resultString.AppendLine("<tr><th>Word</th><th>Count</th></tr>");
            foreach (var word in result.FoundWordFrequencyResultSet.WordDictionary)
            {
                resultString.AppendLine($"<tr><td>{word.Value.FoundWord.Text}</td><td>{word.Value.Hits}</td></tr>");
            }
            resultString.AppendLine("</table>");
            Assert.IsNotNull(result);
        }
    }
